import React from "react";

export default function Contact() {
  return (
    <section className="py-20 px-6 max-w-xl mx-auto text-center">
      <h2 className="text-4xl font-bold text-blue-500 mb-6">Contact</h2>
      <form className="flex flex-col gap-4">
        <input placeholder="Nom" className="p-3 rounded bg-zinc-100 dark:bg-zinc-800 text-black dark:text-white" />
        <input placeholder="Email" className="p-3 rounded bg-zinc-100 dark:bg-zinc-800 text-black dark:text-white" />
        <textarea rows="4" placeholder="Votre message" className="p-3 rounded bg-zinc-100 dark:bg-zinc-800 text-black dark:text-white" />
        <button className="bg-blue-500 hover:bg-blue-600 text-white py-2 rounded-xl font-semibold">Envoyer</button>
      </form>
    </section>
  );
}
