import React, { useEffect, useState } from "react";
import "./novamethode.css";

export default function NovaMethode() {
  useEffect(() => {
    document.title = "NovaMéthode - Notre approche";
    const meta = document.querySelector('meta[name="description"]');
    if (meta) {
      meta.setAttribute("content", "Découvrez la NovaMéthode : une approche structurée et humaine pour créer des sites web performants et sur-mesure.");
    } else {
      const metaTag = document.createElement("meta");
      metaTag.name = "description";
      metaTag.content = "Découvrez la NovaMéthode : une approche structurée et humaine pour créer des sites web performants et sur-mesure.";
      document.head.appendChild(metaTag);
    }
  }, []);

  const [step, setStep] = useState(null);

  return (
    <main className="nova-container">
      <section className="intro">
        <h1>NovaMéthode – Notre approche unique du web</h1>
        <p>Une méthode claire, humaine et performante pour accompagner chaque projet avec rigueur et créativité.</p>
      </section>

      <section className="steps">
        {["Découverte", "Design", "Développement", "Lancement"].map((title, index) => (
          <div
            key={index}
            className={`step ${step === index ? "active" : ""}`}
            onClick={() => setStep(index)}
            aria-expanded={step === index}
          >
            <h2>{index + 1}. {title}</h2>
            {step === index && (
              <p>
                {[
                  "Analyse des besoins, objectifs du client, étude de la concurrence, audit technique et stratégie.",
                  "Wireframes, maquettes Figma, UI/UX, responsive design et validation avec le client.",
                  "Développement React, Tailwind CSS, SEO intégré, performance et accessibilité.",
                  "Mise en production, maintenance, support technique et accompagnement à long terme."
                ][index]}
              </p>
            )}
          </div>
        ))}
      </section>

      <section className="video-section">
        <h2>Découvrez notre méthode en vidéo</h2>
        <video controls className="novamethode-video">
          <source src="/video/novamethode-demo.mp4" type="video/mp4" />
          Votre navigateur ne supporte pas la vidéo HTML5.
        </video>
      </section>
    </main>
);
}
