import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import Home from "./pages/Home/Home";
import Services from "./pages/Services";
import NovaMethode from "./pages/NovaMethode/NovaMethode";
import About from "./pages/About/About";
import Contact from "./pages/Contact";

function App() {
  const [darkMode, setDarkMode] = useState(true);
  return (
    <div className={darkMode ? 'dark' : ''}>
      <div className="min-h-screen">
        {/* Header global supprimé pour éviter la double barre */}
        {/*
        <header className="flex items-center justify-between px-6 py-4 bg-white dark:bg-zinc-900 shadow">
          <div className="flex items-center gap-2">
            <img src="/logo.svg" alt="Logo NovaWeb" className="h-8" />
            <span className="font-bold text-xl text-blue-500">NovaWeb</span>
          </div>
          <nav className="flex gap-4 text-sm items-center">
            <Link to="/">Accueil</Link>
            <Link to="/services">Services</Link>
            <Link to="/realisations">Réalisations</Link>
            <Link to="/about">À propos</Link>
            <Link to="/contact">Contact</Link>
            <button onClick={() => setDarkMode(!darkMode)} className="px-2 py-1 border border-blue-500 text-blue-500 rounded hover:bg-blue-500 hover:text-white transition">
              {darkMode ? 'Clair' : 'Sombre'}
            </button>
          </nav>
        </header>
        */}
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/services" element={<Services />} />
          <Route path="/novaMethode" element={<NovaMethode />} />
          <Route path="/about" element={<About />} />
          <Route path="/contact" element={<Contact />} />
        </Routes>
        <footer className="text-center text-sm py-6 text-gray-400 bg-white dark:bg-zinc-900">
          © 2025 NovaWeb. Tous droits réservés.
        </footer>
      </div>
    </div>
  );
}

export default App;
