import React from 'react';
import { Helmet } from 'react-helmet';

export default function SiteVitrine() {
  return (
    <main className="bg-[#181B20] min-h-screen text-white font-sans">
      <Helmet>
        <title>Création de site vitrine à Nantes | NovaWeb</title>
        <meta name="description" content="Création de site vitrine professionnel à Nantes et Pays de la Loire. Présence en ligne, design moderne, SEO, accompagnement sur-mesure." />
      </Helmet>
      <section className="max-w-4xl mx-auto py-16 px-6">
        <h1 className="text-4xl font-bold mb-8 text-center">Création de site vitrine à Nantes</h1>
        <p className="text-lg text-gray-300 mb-6 text-center">Boostez votre visibilité avec un site vitrine moderne, responsive et optimisé pour Google. Idéal pour artisans, PME, indépendants et associations.</p>
        <ul className="list-disc list-inside text-gray-300 mb-8">
          <li>Design personnalisé et responsive</li>
          <li>Optimisation SEO locale (Nantes, Pays de la Loire)</li>
          <li>Accompagnement de A à Z</li>
          <li>Maintenance et évolutivité</li>
        </ul>
        <p className="text-center"><a href="/contact" className="cta-btn">Demander un devis</a></p>
      </section>
    </main>
  );
}
