import React, { useState } from 'react';
import Header from '../../components/Header';
import './services.css';

const SERVICES = [
  {
    key: 'dev',
    title: 'Développement Web',
    desc: 'Création de sites web sur mesure et performants',
    icon: (
      <svg width="56" height="56" viewBox="0 0 56 56" fill="none" aria-hidden="true">
        <rect x="8" y="16" width="40" height="24" rx="6" fill="#2563eb"/>
        <rect x="14" y="22" width="28" height="12" rx="2" fill="#181B20"/>
        <path d="M24 32l-4 4 4 4" stroke="#fff" strokeWidth="2.5" strokeLinecap="round" strokeLinejoin="round"/>
        <path d="M32 32l4 4-4 4" stroke="#fff" strokeWidth="2.5" strokeLinecap="round" strokeLinejoin="round"/>
        <rect x="22" y="26" width="12" height="4" rx="2" fill="#2563eb"/>
      </svg>
    ),
    details: {
      title: 'Développement Web',
      text: 'Nous créons des sites web à la fois modernes, réactifs et adaptés aux besoins spécifiques de chaque client. En utilisant les dernières technologies, nous garantissons des performances optimales et une gestion aisée.'
    }
  },
  {
    key: 'uiux',
    title: 'Conception UI/UX',
    desc: "Design d'interfaces intuitives et expérience utilisateur",
    icon: (
      <svg width="56" height="56" viewBox="0 0 56 56" fill="none" aria-hidden="true">
        <rect x="12" y="12" width="32" height="32" rx="8" fill="#2563eb"/>
        <path d="M20 36l16-16" stroke="#fff" strokeWidth="3" strokeLinecap="round"/>
        <rect x="26" y="26" width="4" height="16" rx="2" fill="#fff"/>
        <rect x="32" y="16" width="8" height="4" rx="2" fill="#2563eb"/>
      </svg>
    ),
    details: {
      title: 'Conception UI/UX',
      text: "Design d'interfaces intuitives et expérience utilisateur. Nous concevons des interfaces élégantes, accessibles et centrées sur l'utilisateur pour maximiser l'engagement et la satisfaction."
    }
  },
  {
    key: 'seo',
    title: 'Référencement',
    desc: "Optimization pour les moteurs de recherche (SEO)",
    icon: (
      <svg width="56" height="56" viewBox="0 0 56 56" fill="none" aria-hidden="true">
        <path d="M16 44V38C16 34.6863 18.6863 32 22 32H34C37.3137 32 40 34.6863 40 38V44" stroke="#2563eb" strokeWidth="3" strokeLinecap="round"/>
        <rect x="22" y="18" width="12" height="12" rx="6" fill="#2563eb"/>
        <path d="M28 12v-2M28 30v2M22 24h-2M34 24h2M23.757 19.757l-1.414-1.414M32.243 28.243l1.414 1.414M23.757 28.243l-1.414 1.414M32.243 19.757l1.414-1.414" stroke="#2563eb" strokeWidth="2"/>
      </svg>
    ),
    details: {
      title: 'Référencement',
      text: "Nous optimisons votre site pour le référencement naturel afin d'augmenter votre visibilité sur Google et générer plus de trafic qualifié."
    }
  }
];

export default function Services() {
  const [open, setOpen] = useState(null);

  return (
    <main className="bg-[#181B20] min-h-screen text-white font-sans flex flex-col min-h-screen">
      {/* SEO */}
      <head>
        <title>Services - NovaWeb</title>
        <meta name="description" content="Découvrez les services professionnels de NovaWeb : création de sites, référencement, design et plus." />
      </head>
      <Header />
      <section className="flex-1 max-w-6xl mx-auto py-14 px-4 w-full">
        <h1 className="text-5xl font-extrabold text-center text-white mb-4">Nos Services</h1>
        <p className="text-center text-gray-300 mb-12 text-lg">Nous proposons une gamme de services pour répondre à vos besoins numériques.</p>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8 mb-8">
          {SERVICES.map((service, idx) => (
            <button
              key={service.key}
              className={`service-card group relative flex flex-col items-center text-center rounded-xl p-10 bg-[#23262B] transition duration-300 ease-in-out cursor-pointer focus:outline-none focus:ring-2 focus:ring-blue-600 shadow-lg ${open === idx ? 'ring-2 ring-blue-600 scale-105' : ''}`}
              aria-expanded={open === idx}
              aria-controls={`service-details-${service.key}`}
              onClick={() => setOpen(open === idx ? null : idx)}
              tabIndex={0}
              type="button"
            >
              <span className="mb-4">{service.icon}</span>
              <h2 className="text-2xl font-extrabold text-white mb-2">{service.title}</h2>
              <p className="text-gray-300 text-lg">{service.desc}</p>
              <span className="absolute top-4 right-4 text-blue-500 opacity-80 group-hover:scale-125 transition-transform duration-300">
                {open === idx ? (
                  <svg width="24" height="24" viewBox="0 0 24 24" fill="none"><path d="M6 15l6-6 6 6" stroke="#2563eb" strokeWidth="2.5" strokeLinecap="round"/></svg>
                ) : (
                  <svg width="24" height="24" viewBox="0 0 24 24" fill="none"><path d="M12 8v8M8 12h8" stroke="#2563eb" strokeWidth="2.5" strokeLinecap="round"/></svg>
                )}
              </span>
            </button>
          ))}
        </div>
        {/* Section de détails animée */}
        <div className="relative">
          {SERVICES.map((service, idx) => (
            <div
              key={service.key}
              id={`service-details-${service.key}`}
              role="region"
              aria-labelledby={`service-title-${service.key}`}
              className={`service-details ${open === idx ? 'open' : ''}`}
              style={{
                maxHeight: open === idx ? 400 : 0,
                opacity: open === idx ? 1 : 0,
                pointerEvents: open === idx ? 'auto' : 'none',
                transition: 'all 0.4s cubic-bezier(.4,0,.2,1)',
                overflow: 'hidden',
                marginBottom: open === idx ? 32 : 0
              }}
            >
              {open === idx && (
                <div className="flex flex-col items-center justify-center text-center bg-transparent animate-fade-in py-8">
                  <h2 className="text-4xl font-extrabold text-white mb-4">{service.details.title}</h2>
                  <p className="text-gray-200 text-lg max-w-2xl mx-auto">{service.details.text}</p>
                </div>
              )}
            </div>
          ))}
        </div>
      </section>
      <footer className="bg-[#181B20] border-t border-[#23262B] py-8 px-8 mt-8 text-center text-gray-500 text-sm w-full">
        © 2025 NovaWeb. Tous droits réservés.
      </footer>
    </main>
  );
}
