import React, { useEffect, useState } from "react";
import Header from "../../components/Header";
import "./novamethode.css";

export default function NovaMethode() {
  useEffect(() => {
    document.title = "NovaMéthode - Notre approche";
    const meta = document.querySelector('meta[name="description"]');
    if (meta) {
      meta.setAttribute("content", "Découvrez la NovaMéthode : une approche structurée et humaine pour créer des sites web performants et sur-mesure.");
    } else {
      const metaTag = document.createElement("meta");
      metaTag.name = "description";
      metaTag.content = "Découvrez la NovaMéthode : une approche structurée et humaine pour créer des sites web performants et sur-mesure.";
      document.head.appendChild(metaTag);
    }
  }, []);

  const steps = [
    {
      title: "Découverte",
      desc: "Nous commençons par une phase d'écoute active pour comprendre vos besoins, vos objectifs et l'ADN de votre entreprise. Nous analysons la concurrence, réalisons un audit technique de l'existant et définissons ensemble une stratégie digitale sur-mesure. Cette étape garantit que chaque décision prise par la suite est alignée avec votre vision et vos ambitions."
    },
    {
      title: "Design",
      desc: "Nous concevons des wireframes et des maquettes sur Figma pour imaginer une interface à la fois élégante, intuitive et centrée sur l'utilisateur. Chaque détail du design est pensé pour valoriser votre image de marque, optimiser l'expérience utilisateur (UX) et garantir une parfaite adaptabilité sur tous les supports (responsive). Vous validez chaque étape pour avancer en toute confiance."
    },
    {
      title: "Développement",
      desc: "Nos développeurs réalisent votre site avec les technologies les plus modernes (React, Tailwind CSS) pour garantir rapidité, sécurité et évolutivité. Nous intégrons les meilleures pratiques SEO dès la conception, optimisons la performance et veillons à l'accessibilité pour tous les utilisateurs. Chaque fonctionnalité est testée et validée pour une qualité irréprochable."
    },
    {
      title: "Lancement",
      desc: "Après une phase de recette rigoureuse, nous mettons votre site en ligne dans les meilleures conditions. Nous assurons la maintenance, le suivi technique et restons à vos côtés pour faire évoluer votre projet. Notre accompagnement sur le long terme vous garantit sérénité, sécurité et performance durable."
    }
  ];

  const [step, setStep] = useState(0);

  return (
    <main className="nova-container bg-[#181B20] min-h-screen text-white font-sans">
      <Header />
      <section className="intro max-w-3xl mx-auto mt-10 mb-8">
        <h1 className="text-4xl md:text-5xl font-extrabold text-center text-blue-500 mb-6">NovaMéthode</h1>
        <h2 className="text-2xl md:text-3xl font-semibold text-center text-white mt-2 mb-6">Notre approche unique du web</h2>
        <p className="text-center text-gray-300 text-lg mb-8">Une méthode claire, humaine et performante pour accompagner chaque projet avec rigueur et créativité.</p>
      </section>

      <section className="steps max-w-2xl mx-auto w-full mb-12">
        {steps.map((s, index) => (
          <div
            key={index}
            className={`step-card group mb-5 rounded-xl shadow-lg bg-[#1f2937] transition cursor-pointer ${step === index ? "ring-2 ring-blue-600 scale-105" : "hover:bg-[#232b36]"}`}
            onClick={() => setStep(index)}
            aria-expanded={step === index}
            tabIndex={0}
          >
            <div className="flex items-center gap-4 px-6 py-5">
              <div className={`step-number flex items-center justify-center rounded-full font-bold text-lg w-10 h-10 ${step === index ? "bg-blue-600 text-white" : "bg-[#232b36] text-blue-400"}`}>{index + 1}</div>
              <h2 className="text-xl font-semibold text-white">{s.title}</h2>
            </div>
            {step === index && (
              <div className="step-detail px-6 pb-5 animate-fade-in">
                <p className="text-gray-300 text-base mt-2">{s.desc}</p>
              </div>
            )}
          </div>
        ))}
      </section>

      <section className="video-section max-w-2xl mx-auto w-full mb-16">
        <h2 className="text-2xl font-bold text-center text-white mb-4">Découvrez notre méthode en vidéo</h2>
        <video controls className="novamethode-video mx-auto">
          <source src="/video/novamethode-demo.mp4" type="video/mp4" />
          Votre navigateur ne supporte pas la vidéo HTML5.
        </video>
      </section>
    </main>
  );
}
