import React from "react";

const services = [
  { title: "Site vitrine", description: "Création de sites professionnels et responsive pour valoriser votre activité." },
  { title: "Applications Web", description: "Développement de plateformes sur mesure, rapides et sécurisées." },
  { title: "SEO & Performance", description: "Optimisation technique pour améliorer votre position sur Google." }
];

export default function Services() {
  return (
    <section className="py-20 px-6 max-w-6xl mx-auto">
      <h2 className="text-4xl font-bold text-blue-500 mb-10 text-center">Nos Services</h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {services.map((service, idx) => (
          <div key={idx} className="p-6 bg-zinc-100 dark:bg-zinc-800 rounded-xl shadow hover:shadow-xl transition">
            <h3 className="text-xl font-semibold mb-2">{service.title}</h3>
            <p className="text-sm text-gray-500 dark:text-gray-300">{service.description}</p>
          </div>
        ))}
      </div>
    </section>
  );
}
