import React, { useState } from 'react';
import Header from '../../components/Header';
import './services.css';
import { Code2, PencilLine, LineChart } from "lucide-react";

const SERVICES = [
  {
    key: 'dev',
    title: 'Développement Web',
    desc: 'Création de sites web sur mesure et performants',
    icon: (
      <div className="bg-blue-600 rounded-full p-3 mb-4">
        <Code2 size={32} color="#fff" strokeWidth={2} />
      </div>
    ),
    details: {
      title: 'Développement Web',
      text: "Confiez-nous la création de votre site web sur-mesure : design moderne, performance optimale, sécurité et évolutivité. Nous développons des plateformes robustes, rapides et parfaitement adaptées à votre activité pour maximiser votre impact en ligne et générer plus de clients."
    }
  },
  {
    key: 'uiux',
    title: 'Conception UI/UX',
    desc: "Design d'interfaces intuitives et expérience utilisateur",
    icon: (
      <div className="bg-blue-600 rounded-full p-3 mb-4">
        <PencilLine size={32} color="#fff" strokeWidth={2} />
      </div>
    ),
    details: {
      title: 'Conception UI/UX',
      text: "Offrez à vos visiteurs une expérience unique : nous concevons des interfaces élégantes, intuitives et centrées sur l'utilisateur. Notre expertise UI/UX valorise votre image, augmente l'engagement et transforme vos prospects en clients fidèles."
    }
  },
  {
    key: 'seo',
    title: 'Référencement',
    desc: 'Optimisation pour les moteurs de recherche (SEO)',
    icon: (
      <div className="bg-blue-600 rounded-full p-3 mb-4">
        <LineChart size={32} color="#fff" strokeWidth={2} />
      </div>
    ),
    details: {
      title: 'Référencement',
      text: "Boostez votre visibilité sur Google : nous optimisons votre site pour le référencement naturel (SEO) afin d'attirer plus de visiteurs qualifiés et de générer des opportunités concrètes pour votre business. Gagnez des positions et devancez vos concurrents !"
    }
  }
];

export default function Services() {
  const [open, setOpen] = useState(0);

  return (
    <main className="bg-[#181B20] min-h-screen text-white font-sans flex flex-col min-h-screen">
      {/* SEO */}
      <head>
        <title>Services - NovaWeb</title>
        <meta name="description" content="Découvrez les services professionnels de NovaWeb : création de sites, référencement, design et plus." />
      </head>
      <Header />
      <section className="flex-1 max-w-6xl mx-auto py-14 px-4 w-full">
        <h1 className="text-5xl font-extrabold text-center text-white mb-4">Nos Services</h1>
        <p className="text-center text-gray-300 mb-12 text-lg">Nous proposons une gamme de services pour répondre à vos besoins numériques.</p>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8 mb-8">
          {SERVICES.map((service, idx) => (
            <button
              key={service.key}
              className={`service-card group relative flex flex-col items-center text-center rounded-xl p-8 bg-[#23262B] transition duration-300 ease-in-out cursor-pointer focus:outline-none focus:ring-2 focus:ring-blue-600 shadow-lg ${open === idx ? 'ring-2 ring-blue-600 scale-105' : ''}`}
              aria-expanded={open === idx}
              aria-controls={`service-details-${service.key}`}
              onClick={() => setOpen(idx)}
              tabIndex={0}
              type="button"
            >
              <div className="bg-blue-600 rounded-full p-3 mb-4">
                {service.icon}
              </div>
              <h2 className="font-semibold text-lg mb-2 text-white">{service.title}</h2>
              <p className="text-gray-400 text-sm">{service.desc}</p>
              <span className="absolute top-4 right-4 text-blue-500 opacity-80 group-hover:scale-125 transition-transform duration-300">
                {open === idx ? (
                  <svg width="24" height="24" viewBox="0 0 24 24" fill="none"><path d="M6 15l6-6 6 6" stroke="#2563eb" strokeWidth="2.5" strokeLinecap="round"/></svg>
                ) : (
                  <svg width="24" height="24" viewBox="0 0 24 24" fill="none"><path d="M12 8v8M8 12h8" stroke="#2563eb" strokeWidth="2.5" strokeLinecap="round"/></svg>
                )}
              </span>
            </button>
          ))}
        </div>
        {/* Section de détails animée */}
        <div className="relative">
          {SERVICES.map((service, idx) => (
            <div
              key={service.key}
              id={`service-details-${service.key}`}
              role="region"
              aria-labelledby={`service-title-${service.key}`}
              className={`service-details ${open === idx ? 'open' : ''}`}
              style={{
                maxHeight: open === idx ? 400 : 0,
                opacity: open === idx ? 1 : 0,
                pointerEvents: open === idx ? 'auto' : 'none',
                transition: 'all 0.4s cubic-bezier(.4,0,.2,1)',
                overflow: 'hidden',
                marginBottom: open === idx ? 32 : 0
              }}
            >
              {open === idx && (
                <div className="flex flex-col items-center justify-center text-center bg-transparent animate-fade-in py-8">
                  <h2 className="text-4xl font-extrabold text-white mb-4">{service.details.title}</h2>
                  <p className="text-gray-200 text-lg max-w-2xl mx-auto mb-8">{service.details.text}</p>
                  <a href="/contact" className="inline-block bg-blue-600 hover:bg-blue-700 text-white font-semibold px-7 py-3 rounded-lg shadow transition">Demander un devis</a>
                </div>
              )}
            </div>
          ))}
        </div>
      </section>
      <footer className="bg-[#181B20] border-t border-[#23262B] py-8 px-8 mt-8 text-center text-gray-500 text-sm w-full">
        © 2025 NovaWeb. Tous droits réservés.
      </footer>
    </main>
  );
}
